  },

  methods: {
    drawDot(dot) {
      this.ctx.beginPath();
      this.ctx.arc(dot.x, dot.y, dot.radius, 0, 2 * Math.PI);
      this.ctx.fillStyle = '#F03C69';
      this.ctx.fill();
    },

    moveDot() {
      var dots = this.dots; // Notice in the moveDot function we can make dots go faster if we increment
      // by more than 1 pixel each time.

      var frameLength = 2;
      this.ctx.clearRect(0, 0, window.innerWidth, window.innerHeight); // Iterate over all the dots.

      for (let i = 0; i < dots.length; i++) {
        if (dots[i].xMove == '+') {
          dots[i].x += frameLength;
        } else {
          dots[i].x -= frameLength;
        }

        if (dots[i].yMove == '+') {
          dots[i].y += frameLength;
        } else {
          dots[i].y -= frameLength;
        }

        this.drawDot(dots[i]);

        if (dots[i].x + dots[i].radius >= window.innerWidth) {
          dots[i].xMove = '-';
        }

        if (dots[i].x - dots[i].radius <= 0) {
          dots[i].xMove = '+';
        }

        if (dots[i].y + dots[i].radius >= window.innerHeight) {
          dots[i].yMove = '-';
        }

        if (dots[i].y - dots[i].radius <= 0) {
          dots[i].yMove = '+';
        }
      }
